mapper.select:
SELECT szs.DM AS szsdm, k.DM AS kqdm, s.DM AS xxdm, s.MC AS xxmc, b.BJBS AS bjbs, b.BJMC AS bjmc, b.JB AS jb, b.BZRXM AS bzrxm, b.BJLX AS bjlx, b.YSYZ AS ysyz, b.MZYYSKYZ AS mzyyskyz, szs.MC AS szsmc, k.MC AS kqmc FROM BJXX b JOIN XYZDK s ON b.XXDM = s.DM AND s.JH = 'ZX' -- 学校 JOIN XYZDK k ON b.XXDM LIKE k.DM || '%' AND k.JH = 'KD' -- 考区 JOIN XYZDK szs ON b.XXDM LIKE szs.DM || '%' AND szs.JH = 'KQ' -- 地市
AND b.JB = #{jbny} AND s.DM = #{xxdm} AND k.DM = #{kqdm} AND szs.DM = #{szsdm}
ORDER BY s.DM, b.JB, b.BJMC

mapper.update:
UPDATE bjxx
BJMC = #{bjmc}, jb = #{jb}, BZRXM = #{bzrxm}, BJLX = #{bjlx}, YSYZ = #{ysyz}, MZYYSKYZ = #{mzyyskyz},
WHERE BJBS = #{bjbs}

mapper.select:
SELECT BJMC, JB
FROM bjxx
WHERE BJBS = #{bjbs}

mapper.update:
UPDATE ksxx

    BJBS = #{bjbs},
    BJMC = #{bjmc},
    JB = #{jb}

WHERE KSH IN

    #{ksh}

mapper.select:
SELECT bj.*
FROM bjxx bj
         LEFT JOIN xxjbxx xx ON bj.XXDM = xx.XXDM
WHERE xx.XXMC = #{xxmc}
  AND bj.BJMC = #{bjmc}
